import PostService from "@/helpers/PostService";
export default {
  name: "ContactView",
  data: () => ({
    valid: false,
    success: false,
    fullname: "",
    nameRules: [v => !!v || "Name is required", v => v.length > 1 || "Name must be more than 1 character."],
    message: "",
    messageRules: [v => !!v || "A message is required", v => v.length > 1 || "Message must be more than 1 character."],
    email: "",
    emailRules: [v => !!v || "E-mail is required", v => /.+@.+/.test(v) || "E-mail must be valid (like john@email.com)"]
  }),
  methods: {
    async submit() {
      this.$refs.form.validate();
      if (this.valid) {
        await PostService.insertPost(this.fullname, this.email, this.message);
        this.success = true;
      }
      this.valid = false;
    },
    clear() {
      this.fullname = "";
      this.message = "";
      this.email = "";
    }
  },
  mounted() {
    console.log(this.valid, this.fullname);
  },
  updated() {
    console.log(this.valid, this.fullname);
  }
};
